insert into activity_category(activity_category_id, code, name, description) select 1, 'EDUCATION', 'Education', 'Such as classes in English, maths, construction and computer skills' where not exists (select 1 from activity_category where activity_category_id = 1);
insert into activity_category(activity_category_id, code, name, description) select 2, 'INDUSTRIES', 'Industries', 'Such as work in the prison and with employers and charities' where not exists (select 1 from activity_category where activity_category_id = 2);
insert into activity_category(activity_category_id, code, name, description) select 3, 'SERVICES', 'Services', 'Such as work in the kitchens and laundry, cleaning, gardening, and mentoring' where not exists (select 1 from activity_category where activity_category_id = 3);
insert into activity_category(activity_category_id, code, name, description) select 4, 'GYM_SPORTS_FITNESS', 'Gym, sport and fitness', 'Such as sport clubs, like football, fitness classes and gym sessions' where not exists (select 1 from activity_category where activity_category_id = 4);
insert into activity_category(activity_category_id, code, name, description) select 5, 'INDUCTION', 'Induction', 'Such as gym induction, education assessments, health and safety workshops' where not exists (select 1 from activity_category where activity_category_id = 5);
insert into activity_category(activity_category_id, code, name, description) select 6, 'INTERVENTIONS', 'Intervention programmes', 'Such as programmes for behaviour management, drug and alcohol misuse and community rehabilitation' where not exists (select 1 from activity_category where activity_category_id = 6);
insert into activity_category(activity_category_id, code, name, description) select 7, 'LEISURE_SOCIAL', 'Leisure and social', 'Such as association, library time and social clubs, like music or art' where not exists (select 1 from activity_category where activity_category_id = 7);
